                </Goto>
              </Statements>
            </SwitchSection>
            <SwitchSection>
              <Locals>
                <Parameter Type=""System.Int32"" Id=""3"" Name=""x"" />
              </Locals>
              <Labels>
                <SwitchLabel>
                  <Label>
                    <LabelTarget Type=""System.Void"" Id=""4"" Name=""case int x when x &gt; 0:"" />
                  </Label>
                  <Pattern>
                    <DeclarationPattern InputType=""System.Object"" NarrowedType=""System.Int32"" Type=""System.Int32"">
                      <Variable>
                        <Parameter Type=""System.Int32"" Id=""3"" Name=""x"" />
                      </Variable>
                    </DeclarationPattern>
                  </Pattern>
                  <WhenClause>
