              </Statements>
            </SwitchSection>
            <SwitchSection>
              <Locals>
                <Parameter Type=""System.Object"" Id=""5"" Name=""t"" />
              </Locals>
              <Labels>
                <SwitchLabel>
                  <Label>
                    <LabelTarget Type=""System.Void"" Id=""6"" Name=""case var t:"" />
                  </Label>
                  <Pattern>
                    <VarPattern InputType=""System.Object"" NarrowedType=""System.Object"">
                      <Variable>
                        <Parameter Type=""System.Object"" Id=""5"" Name=""t"" />
                      </Variable>
