                        <Parameter Type=""System.Object"" Id=""5"" Name=""t"" />
                      </Variable>
                    </VarPattern>
                  </Pattern>
                </SwitchLabel>
              </Labels>
              <Statements>
                <Call Type=""System.Void"" Method=""Void WriteLine(System.Object)"">
                  <Arguments>
                    <Parameter Type=""System.Object"" Id=""5"" Name=""t"" />
                  </Arguments>
                </Call>
